import React, {Component} from 'react'

class ProductItem extends Component {
    render() {
        let {id, name, desc, price} = this.props.product
        let image = `http://localhost:8888/${this.props.product.image}`
        return(
            <div className="card col-6 mb-3">
                <img src={image} alt="" className="card-img-top"/>
                <div className="card-body">
                    <h5 style={{color: "#878787"}} className="card-title">{name}</h5>
                    <p className="card-text">{parseInt(price).toLocaleString('IN')}</p>
                </div>
            </div>
        )
    }
}

export default ProductItem
