import React, {Component} from 'react'
import Axios from 'axios'

const URL_API = 'http://localhost:8888/'

class ProductDetail extends Component {
    state = {
        products: []
    }

    componentDidMount() {
        this.getProductData()
    }

    getProductData = () => {
        Axios.get(
            URL_API + `products/productdetail/${this.props.match.params.id}`
        ).then((res) => {
            this.setState({products: res.data})
            // console.log(this.state.products)
        }).catch((err) => {
            console.log(err)
        })
    }

    render() {
        if(this.state.products) {
            let image = `http://localhost:8888/${this.state.product.image}`
            return (
                <div className='card col-5 my-5 mx-auto'>
                    <div className='card-header mt-2'>
                        <h2>{this.state.product.name}</h2>
                    </div>
                    <div className='card-body'>
                        <img className='card-img-top' alt="" src={image}/>
                        <h3>Name: {this.state.product.name}</h3>
                        <p>Description : {this.state.product.desc}</p>
                        <p>Harga : Rp.{this.state.product.price}</p>
                    </div>
                    <form><input className='form-control' type='text'/></form>
                    <button className='btn btn-outline-secondary mt-2'>Add To Cart</button>
                </div>
            )
        } else {
            return <div><h1 className='text-center'>Loading</h1></div>
        }
    }
}

export default ProductDetail
