import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Redirect} from 'react-router-dom'
import '../styles/Cart.css'
import Axios from 'axios'

const URL_API = 'http://localhost:8888/'

class Cart extends Component {
    state = {
        carts: []
    }

    componentDidMount() {
        this.getCartData()
    }

    getCartData = () => {
        Axios.get(
            URL_API + 'carts/getcart',
            {
                params: {
                    userId: this.props.userId
                }
            }
        ).then((res) => {
            this.setState({carts: res.data})
            console.log(this.state.carts)
        }).catch((err) => {
            console.log(err)
        })
    }

    onDeleteClick = (id) => {
        Axios.delete(
            URL_API + `carts/deletecart/${id}`
        ).then((res) => {
            this.getCartData()
            console.log(res.data)
        })
    }

    renderCart = () => {
        return this.state.carts.map((val) => {
            let image = URL_API + val.productImage
            return (
                <tr key={val.id}>
                    <td>
                        <img src={image} alt="" width="200px"/>
                    </td>
                    <td>{val.productName}</td>
                    <td>{val.productPrice}</td>
                    <td>{val.quantity}</td>
                    <td>{val.quantity * val.productPrice}</td>
                    <td><button onClick={() => {this.onDeleteClick(val.id)}} className="button-ku">Delete</button></td>
                </tr>
            )
        })
    }

    render() {
        if(this.props.role === 'user') {
            return(
                <div>
                    <div className="container">
                        <h4 className="mt-5">Shopping Cart</h4>
                        <table className="table">
                            <thead>
                                <tr>
                                    <th>Image</th>
                                    <th>Product</th>
                                    <th>Price</th>
                                    <th>Quantity</th>
                                    <th>Total</th>
                                    <th></th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.renderCart()}
                            </tbody>
                        </table>

                        <div className="text-center">
                            <button className="button-ku-checkout btn-block">Checkout</button>
                        </div>
                    </div>
                </div>
            )
        } else {
            return <Redirect to="/" />
        }
    }
}

const mapStateToProps = (state) => {
    return {
        userId: state.auth.id,
        role: state.auth.role
    }
}

export default connect(mapStateToProps)(Cart)
