const INITIAL_STATE = {
    id: '',
    email: '',
    role: '',
    statusCode: '',
    errorMessage: ''
}

export default(state = INITIAL_STATE, action) => {
    switch (action.type) {
        case 'LOGIN_SUCCESS':
            return {...state, id: action.payload.id, email: action.payload.email, role: action.payload.role}
        case 'LOGOUT_SUCCESS':
            return {state}
        default:
            return state
    }
}
