import React, { Component } from 'react'
import './styles/Cart.css'

class Checkout extends Component {
    state = {
        totalPrice: 0
    }

    renderCheckout = () => {
        return this.props.carts.map((val) => {
            return(
                <tr key={val.id}>
                    <td>{val.id}</td>
                    <td>{val.productName}</td>
                    <td>{val.productPrice}</td>
                    <td>{val.quantity}</td>
                    <td>{val.productPrice * val.quantity}</td>
                </tr>
            )
        })
    }

    renderTotalPrice = () => {
        let total = 0
        let carts = this.props.carts

        for(let i = 0; i < carts.length; i++) {
            total += (carts[i].quantity * carts[i].productPrice)
        }

        this.setState({totalPrice: total})

        return(
            <tr>
                <th colSpan="4">Total</th>
                <td>{total.toLocaleString('IN')}</td>
            </tr>
        )
    }

    render() {
        if(this.props.carts) {
            return (
                <div>
                    <div className="container">
                        <h4 className="mt-5">Checkout</h4>
                        <table className="table text-center">
                            <thead>
                                <tr>
                                    <th>ID</th>
                                    <th>Product</th>
                                    <th>Price</th>
                                    <th>Quantity</th>
                                    <th>Total</th>
                                </tr>
                            </thead>
                            <tbody>
                                {this.renderCheckout()}
                                {this.renderTotalPrice()}
                            </tbody>
                        </table>
                        <button className="button-ku-checkout btn-block">Complete Order</button>
                    </div>
                </div>
            )
        } else {
            return null
        }
    }
}

export default Checkout
