import React, { Component } from 'react'
import {connect} from 'react-redux'
import Axios from 'axios'
import '../styles/AccountInfo.css'

const URL_API = 'http://localhost:8888/'

class AccountInfo extends Component {
    state = {
        transactions: []
    }

    componentDidMount() {
        this.getTransactionUser()
    }

    getTransactionUser = () => {
        Axios.get(
            URL_API + 'transactions/getuserorder',
            {
                params: {
                    userId: this.props.userId
                }
            }
        ).then((res) => {
            this.setState({transactions: res.data})
        }).catch((err) => {
            console.log(err)
        })
    }

    renderTransactionUser = () => {
        return this.state.transactions.map((val) => {
            let totalPrice = val.totalPrice
            return(
                <tr key={val.id}>
                    <td>#{val.id}</td>
                    <td>{val.transactionDate}</td>
                    <td>Rp {totalPrice.toLocaleString('IN')}</td>
                    <td>
                        <input
                            type="file"
                            accept="image/*"
                            ref="uploadImage"
                            hidden
                            onChange={(e) => {this.setState({productImage: e.target.files[0]})}}
                        />
                        <input
                            type="button"
                            value="Upload Receipt"
                            className=""
                            onClick={() => {this.refs.uploadImage.click()}}
                        />
                        <button>Save</button>
                    </td>
                </tr>
            )
        })
    }

    render() {
        return (
            <div>
                <div className="container">
                    <h3 className="mt-5">{this.props.firstName} {this.props.lastName}</h3> <hr/>
                    <h5>Transaction History</h5>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Order</th>
                                <th>Date</th>
                                <th>Total</th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {this.renderTransactionUser()}
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        userEmail: state.auth.email,
        userId: state.auth.id,
        userRole: state.auth.role,
        firstName: state.auth.firstName,
        lastName: state.auth.lastName
    }
}

export default connect(mapStateToProps)(AccountInfo)
