import React, {Component} from 'react'
import {connect} from 'react-redux'
import {Redirect} from 'react-router-dom'

import './ManageProducts.css'

class ManageProducts extends Component {
    state = {
        productName: '',
        productDesc: '',
        productPrice: '',
        productImage: null
    }

    render() {
        if(this.props.role === 'administrator') {
            return(
                <div>
                    {/* <h1 className="text-center">Manage Your Product</h1> */}
                    <div className="container">
                        <h5 className="mt-3">Add new Product</h5>
                        <table className="table">
                            <tr style={{paddingLeft: 0, paddingRight: 0}}>
                                <td><input type="text" className="form-control" placeholder="Product Name"/></td>
                                <td><input type="text" className="form-control" placeholder="Description"/></td>
                                <td><input type="text" className="form-control" placeholder="Price"/></td>
                                <td>
                                    <input
                                        type="file"
                                        accept="image/*"
                                        ref="uploadImage"
                                        hidden
                                        onChange={(e) => {this.setState({productImage: e.target.files[0]})}}
                                    />
                                    <input
                                        type="button"
                                        value="Choose an image"
                                        className="button-ku"
                                        onClick={() => {this.refs.uploadImage.click()}}
                                    />
                                </td>
                                <td><button className="button-ku">Add Product</button></td>
                            </tr>
                        </table>

                        <h5>Our Products</h5>
                        <table className="table">
                            
                        </table>

                    </div>
                </div>
            )
        } else {
            return <Redirect to="/"/>
        }
    } 
}

const mapStateToProps = (state) => {
    return {
        role: state.auth.role
    }
}

export default connect(mapStateToProps, {})(ManageProducts)
